import React, { useState, useEffect } from 'react';
import './GestionUsuariosCard.css';
import { cambiarEstadoUsuario, obtenerImagenUsuarioEmpleado } from '../../../../services/GestionUsuariosService'; // Ajusta la ruta según sea necesario
import Switch from '@mui/material/Switch';
import axios from 'axios';
import { Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Button } from '@mui/material';

export const GestionUsuariosCard = ({ user, onClose, actualizarUsuarios }) => {
    const [estadoUsuario, setEstadoUsuario] = useState(user.estado_usuario);
    const [profileImage, setProfileImage] = useState(user.profile_image || '../img/isotipo-arsheep.png');
    const [selectedImage, setSelectedImage] = useState(null);
    const [open, setOpen] = useState(false);
    const [action, setAction] = useState(null);

    useEffect(() => {

    }, [user.id_usuario]);

    const handleEstadoChange = async () => {
        const nuevoEstado = !estadoUsuario;
        setEstadoUsuario(nuevoEstado);

        try {
            const resultado = await cambiarEstadoUsuario(user.id_usuario, nuevoEstado);
            if (resultado) {
                actualizarUsuarios(); // Actualiza la lista de usuarios
            }
        } catch (error) {
            console.error('Error al cambiar el estado del usuario:', error);
        }
    };

    const handleImageUpload = async () => {
        if (!selectedImage) return;

        const formData = new FormData();
        formData.append('image', selectedImage);
        formData.append('id_usuario', user.id_usuario);

        try {
            const response = await axios.post('http://localhost:3001/api/usersEmployees/uploadImage', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });

            const newImageUrl = response.data.url;
            setProfileImage(newImageUrl); // Actualizar la imagen de perfil con la nueva URL
            actualizarUsuarios();
            const fetchImage = async () => {
                const image = await obtenerImagenUsuarioEmpleado(user.id_usuario);
                setProfileImage(image);
            };
            fetchImage();
        } catch (error) {
            console.error('Error al subir la imagen:', error);
        }
    };

    const handleImageChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setSelectedImage(file);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleConfirm = async () => {
        if (estadoUsuario !== user.estado_usuario) {
            await handleEstadoChange();
        }
        if (selectedImage) {
            await handleImageUpload();
        }
        setOpen(false);
    };

    return (
        <div className='modal-container'>
            <div className="card">
                <div className="card-header">
                    <span onClick={onClose}>X</span>
                </div>
                <div className="card-title">
                    <h1>Datos del Usuario</h1>
                </div>
                <div className='card-image'>
                    <img
                        src={profileImage}
                        alt={`Imagen de ${user.nombre_usuario}`}
                        onClick={() => document.getElementById('imageUpload').click()}
                    />
                    <input
                        type="file"
                        id="imageUpload"
                        style={{ display: 'none' }}
                        onChange={handleImageChange}
                    />
                </div>
                <div className="card-content">
                    <div className="card-row">
                        <div className="card-item">
                            <strong>ID USUARIO:</strong><span>{user.id_usuario}</span>
                        </div>
                        <div className="card-item">
                            <strong>NOMBRE DE USUARIO:</strong><span>{user.nombre_usuario}</span>
                        </div>
                    </div>
                    <div className="card-row">
                        <div className="card-item">
                            <strong>ID EMPLEADO:</strong><span>{user.id_empleado}</span>
                        </div>
                        <div className="card-item">
                            <strong>FUNCIÓN:</strong><span>{user.EMPLEADO.ROL.desc_rol}</span>
                        </div>
                    </div>
                    <div className="card-row">
                        <div className="card-item">
                            <strong>NOMBRE:</strong><span>{`${user.EMPLEADO.pnombre} ${user.EMPLEADO.apaterno} ${user.EMPLEADO.amaterno}`}</span>
                        </div>
                    </div>
                    <div className="card-row">
                        <div className="card-item">
                            <strong>TELÉFONO:</strong><span>{user.EMPLEADO.numero_contacto}</span>
                        </div>
                    </div>
                </div>
                <div className="card-footer">
                    <label>
                        <Switch
                            checked={estadoUsuario}
                            onChange={() => setEstadoUsuario(!estadoUsuario)}
                            color="primary"
                        />
                        {estadoUsuario ? 'Desactivar Usuario' : 'Activar Usuario'}
                    </label>
                    <div>
                        <button onClick={handleClickOpen}>Guardar</button>
                        <button onClick={onClose}>Cancelar</button>
                    </div>
                </div>
            </div>
            <Dialog
                open={open}
                onClose={handleClose}
            >
                <DialogTitle>{"Guardar cambios"}</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        ¿Estás seguro de que deseas guardar los cambios?
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose} color="primary">
                        Cancelar
                    </Button>
                    <Button onClick={handleConfirm} color="primary">
                        Confirmar
                    </Button>
                </DialogActions>
            </Dialog>
        </div>
    );
};
