import React from 'react';
import './DetalleOrdenTrabajo.css'

export const DetalleOrdenTrabajo = ({ orden }) => {
  return (
    <div className='detalle-orden-trabajo-container'>
      <h4>Detalle de OT {orden.id_ot}</h4>
      <p>{orden.descripcion}</p>
      <p>{orden.status}</p>
      <p>{orden.fecha_creacion}</p>
      <p>{orden.fecha_vencimiento}</p>
      <p style={{ color: getColorPrioridad(orden.prioridad) }}>{orden.prioridad}</p>
    </div>
  );
};

const getColorPrioridad = (prioridad) => {
  switch (prioridad) {
    case 'Baja':
      return 'green';
    case 'Media':
      return 'orange';
    case 'Alta':
      return 'red';
    default:
      return 'white';
  }
};
