import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { obtenerUsuario } from '../../services/supa';

export const Login = () => {

    const [data, setData] = useState([]);


    // useEffect se utiliza para cargar datos al montar el componente.
    useEffect(() => {
        // Función asíncrona para obtener los datos de los clientes.
        const fetchData = async () => {
            const usuarios = await obtenerUsuario(); // Llama a una función que obtiene los clientes.
            setData(usuarios);
            console.log(usuarios);
        };
        fetchData(); // Ejecuta la función fetchData al montar el componente.
    }, []); 




    let iniciarSesion = useNavigate();
    

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) => {

        e.preventDefault(); 

        iniciarSesion('/Clientes');

    };

    return (
        <form onSubmit={handleSubmit}>

            <div>
                <label htmlFor="username">Usuario:</label>
                <input
                    id="username"
                    type="text"
                    value={username}
                    onChange={e => setUsername(e.target.value)} // Actualiza el estado al cambiar el contenido del input
                />
            </div>

            <div>
                <label htmlFor="password">Contraseña:</label>
                <input
                    id="password"
                    type="password"
                    value={password}
                    onChange={e => setPassword(e.target.value)} // Actualiza el estado al cambiar el contenido del input
                />
            </div>
            
            <button type="submit">Iniciar sesión</button>
        </form>
    );

}
