import React, { useEffect, useState } from 'react';
import './CrearVisitaTecnica.css';
import { getVisitasTecnicas } from '../../../services/visitaTecnicaService';
import { getEmpleadoById, getEmpleados } from '../../../services/empleadoService';


export const CrearVisitaTecnica = () => {

  let [visitas, setVisitas] = useState([]);
  let [empleados, setEmpleados] = useState([]);
  let [empleado,setEmpleado] = useState({});


  const fetchData = async () => {
    try {
      let data = await getVisitasTecnicas();
      setVisitas(data);
      data = await getEmpleados();
      setEmpleados(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };


  const fetchEmpleado = async (id) => {
    try {
      let empleado = await getEmpleadoById(id);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {

    fetchData()
    let emp = fetchEmpleado(3)
    setEmpleado(emp)
    console.log(emp);

  }, [swdc])


  const [formData, setFormData] = useState({
    id_vt: '',
    desc_vt: '',
    desc_problema_vt: '',
    analisis_vt: '',
    recomendacion_vt: '',
    beneficio_vt: '',
    proximospasos_vt: '',
    fec_creacion_vt: '',
    numrun_cliente: '',
    id_empleado: '',
    id_ot: '',
    id_estado_vt: '',
    fec_programacion_vt: '',
  });


  const estados = [
    { id: 1, nombre: 'Estado 1' },
    { id: 2, nombre: 'Estado 2' },
    // Añadir más estados según sea necesario
  ];

  const clientes = [
    { id: 1, nombre: 'Cliente 1' },
    { id: 2, nombre: 'Cliente 2' },
    // Añadir más clientes según sea necesario
  ];

  const ots = [
    { id: 1, nombre: 'OT 1' },
    { id: 2, nombre: 'OT 2' },
    // Añadir más órdenes de trabajo según sea necesario
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: name === 'id_empleado' || name === 'numrun_cliente' || name === 'id_ot' || name === 'id_estado_vt' ? parseInt(value, 10) : value });
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
  };

  return (
    <div className='visita-tecnica-crear-container'>
      <div className='visita-tecnica-crear-principal'>
        <div className='visita-tecnica-crear-header'>
          <h2>CREAR VISITA TÉCNICA</h2>
        </div>
        <div className='visita-tecnica-crear-content'>
          <div className='visita-tecnica-crear-form-container'>
            <form onSubmit={handleSubmit}>
              <div className='form-header'>
                <label>ID Visita Técnica</label>
                <input type="text" name="id_vt" value={formData.id_vt} onChange={handleChange} />
              </div>
              <div className='form-level-1'>
                <div className='form-group'>
                  <label>Descripción</label>
                  <textarea name="desc_vt" value={formData.desc_vt} onChange={handleChange}></textarea>
                  <label>Descripción del Problema</label>
                  <textarea name="desc_problema_vt" value={formData.desc_problema_vt} onChange={handleChange}></textarea>
                  <label>Análisis</label>
                  <textarea name="analisis_vt" value={formData.analisis_vt} onChange={handleChange}></textarea>
                  <label>Recomendaciones</label>
                  <textarea name="recomendacion_vt" value={formData.recomendacion_vt} onChange={handleChange}></textarea>
                  <label>Beneficios</label>
                  <textarea name="beneficio_vt" value={formData.beneficio_vt} onChange={handleChange}></textarea>
                  <label>Próximos Pasos</label>
                  <textarea name="proximospasos_vt" value={formData.proximospasos_vt} onChange={handleChange}></textarea>
                </div>
              </div>
              <div className='form-level-2'>
                <div className='form-group'>
                  <label>Fecha de Visita</label>
                  <input type="date" name="fec_programacion_vt" value={formData.fec_programacion_vt} onChange={handleChange} />
                </div>
                <div className='form-group'>
                  <label>Cliente</label>
                  <select name="numrun_cliente" value={formData.numrun_cliente} onChange={handleChange}>
                    {clientes.map((cliente) => (
                      <option key={cliente.id} value={cliente.id}>
                        {cliente.nombre}
                      </option>
                    ))}
                  </select>
                </div>
                <div className='form-group'>
                  <label>Empleado</label>
                  <select name="id_empleado" value={formData.id_empleado} onChange={handleChange}>
                    {empleados.map((empleado) => (
                      <option key={empleado.id_empleado} value={empleado.id_empleado}>
                        {empleado.pnombre + " " + empleado.apaterno + " " + empleado.amaterno}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
              <div className='form-level-3'>
                <div className='form-group'>
                  <label>Orden de Trabajo</label>
                  <select name="id_ot" value={formData.id_ot} onChange={handleChange}>
                    {ots.map((ot) => (
                      <option key={ot.id} value={ot.id}>
                        {ot.nombre}
                      </option>
                    ))}
                  </select>
                </div>
                <div className='form-group'>
                  <label>Estado de Visita Técnica</label>
                  <select name="id_estado_vt" value={formData.id_estado_vt} onChange={handleChange}>
                    {estados.map((estado) => (
                      <option key={estado.id} value={estado.id}>
                        {estado.nombre}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
              <div className='form-level-4'>
                <button type="submit">Enviar</button>
              </div>
            </form>
          </div>
        </div>
        <div className='visita-tecnica-crear-footer'>
          <button type="submit" onClick={handleSubmit}>Crear Visita Técnica</button>
        </div>
      </div>
      <div className='visita-tecnica-crear-panel'>
        {formData.fec_programacion_vt && (
          <div>
            <h1>VISITAS PROGRAMADAS</h1>
            <p>EMPLEADO: {formData.EMPLEADO.pnombre + " " + formData.EMPLEADO.apaterno + " " + formData.EMPLEADO.amaterno} </p>
            {visitas.filter(visita => visita.fec_programacion_vt === formData.fec_programacion_vt && visita.id_empleado === formData.id_empleado).map((visita, index) => (
              <div key={index} className="visita-tecnica" onClick={() => ""}>
                <div>
                  <p>PROGRAMADA: {visita.fec_programacion_vt}</p>
                  <h4>VT {visita.id_vt}</h4>
                </div>
                <p>{visita.desc_vt}</p>

              </div>
            ))}
          </div>
        )}

