import React from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

const localizer = momentLocalizer(moment);

const transformarDatosOTParaCalendario = (datosOT) => {
  return datosOT.map(ot => ({
    title: `OT ${ot.descripción}`,
    start: new Date(ot.fecha_creacion), // Usamos solo la fecha de creación
    end: new Date(ot.fecha_creacion), // Usamos solo la fecha de creación
    allDay: true, // Para que el evento se muestre todo el día
    id: ot.id_ot // Opcional: incluimos el ID de la OT como identificador
  }));
};

const OTCalendario = ({ data }) => {
  const eventos = transformarDatosOTParaCalendario(data);

  return (
    <div style={{ height: 500 }}>
      <Calendar
        localizer={localizer}
        events={eventos}
        startAccessor="start"
        endAccessor="end"
        style={{ height: '100%' }}
      />
    </div>
  );
};

export default OTCalendario;
