import React, { useEffect, useState } from 'react';
import './CrearVisitaTecnica.css';
import { createVisitaTecnica, getVisitasTecnicas } from '../../../services/visitaTecnicaService';
import { getEmpleadoById, getEmpleados } from '../../../services/empleadoService';
import { getClientes } from '../../../services/clienteService';
import { getEmpresas } from '../../../services/empresaService';

export const CrearVisitaTecnica = () => {
  // Estados para almacenar datos de visitas técnicas, empleados y un empleado específico
  const [visitas, setVisitas] = useState([]);
  const [empleados, setEmpleados] = useState([]);
  const [clientes, setClientes] = useState([]);
  const [empleado, setEmpleado] = useState({});
  const [empresas, setEmpresas] = useState({});

  // Función para obtener datos de visitas técnicas
  const fetchVisitasTecnicas = async () => {
    try {
      const data = await getVisitasTecnicas();
      setVisitas(data);
    } catch (error) {
      console.error('Error fetching visitas tecnicas:', error);
    }
  };
  
  // Función para obtener datos de todos los empleados
  const fetchEmpleados = async () => {
    try {
      const data = await getEmpleados();
      setEmpleados(data);
    } catch (error) {
      console.error('Error fetching empleados:', error);
    }
  };


    // Función para obtener datos de todos los clientes
    const fetchClientes = async () => {
      try {
        const data = await getClientes();
        setClientes(data);
      } catch (error) {
        console.error('Error fetching clientes:', error);
      }
    };
  
  // Función para obtener datos de un empleado específico por su ID
  const fetchEmpleadoById = async (id) => {
    try {
      const data = await getEmpleadoById(id);
      setEmpleado(data);
    } catch (error) {
      console.error(`Error fetching empleado with id ${id}:`, error);
    }
  };

    // Función para obtener datos de todos los clientes
    const fetchEmpresas = async () => {
      try {
        const data = await getEmpresas();
        setEmpresas(data);
        console.log(empresas)
      } catch (error) {
        console.error('Error fetching empresas:', error);
      }
    };
  
  // Función para orquestar la obtención de datos
  const fetchData = async (id) => {
    await fetchVisitasTecnicas();
    await fetchEmpleados();
    if (id) {
      await fetchEmpleadoById(id);
    }
    await fetchClientes();
    await fetchEmpresas();
  };
  
  // Estado para almacenar los datos del formulario
  const [formData, setFormData] = useState({
    desc_vt: '',
    id_empleado: '',
    fec_programacion_vt: '2022-01-01',
  });

    // Función para manejar cambios en el formulario
    const handleChange = (e) => {
      const { name, value } = e.target;
      setFormData({ 
        ...formData, 
        [name]: name === 'id_empleado' || name === 'numrun_cliente' || name === 'id_ot' || name === 'id_estado_vt' 
          ? parseInt(value, 10) 
          : value 
      });
    };

    
    const handleSubmit = async (e) => {
      e.preventDefault();
      try {
        await createVisitaTecnica(formData);
        console.log('Visita Técnica creada con éxito:', formData);
      } catch (error) {
        console.error('Error al crear la Visita Técnica:', error);
      }
    };





  // Filtrar visitas técnicas basadas en la fecha de programación y el empleado seleccionado
  const visitasFiltradas = visitas.filter(visita => 
    visita.fec_programacion_vt === formData.fec_programacion_vt && 
    visita.id_empleado === formData.id_empleado
  );
  const cantVisitas = visitasFiltradas.length;


  // UseEffect para cargar datos iniciales y cada vez que cambie el id_empleado
  useEffect(() => {
    if (formData.id_empleado) {
      fetchData(formData.id_empleado);
    } else {
      fetchData();
      console.log(empresas)
    }
  }, [formData.id_empleado]);

  return (
    <div className='visita-tecnica-crear-container'>
      <div className='visita-tecnica-crear-principal'>
        <div className='visita-tecnica-crear-header'>
          <h2>CREAR VISITA TÉCNICA</h2>
        </div>
        <div className='visita-tecnica-crear-content'>
          <div className='visita-tecnica-crear-form-container'>
            <form onSubmit={handleSubmit}>
              <div className='form-header'>
                <label>ID Visita Técnica</label>
              </div>
              <div className='form-level-1'>
                <div className='form-group'>
                  <label>Descripción</label>
                  <textarea name="desc_vt" value={formData.desc_vt} onChange={handleChange}></textarea>
                </div>
              </div>
              <div className='form-level-2'>
                <div className='form-group'>
                  <label>Fecha de Visita</label>
                  <input type="date" name="fec_programacion_vt" value={formData.fec_programacion_vt} onChange={handleChange} />
                </div>
                <div className='form-group'>
                  <label>Empleado</label>
                  <select name="id_empleado" value={formData.id_empleado} onChange={handleChange}>
                    <option>SELECCIONE UN EMPLEADO</option>
                    {empleados.map((empleado) => (
                      <option key={empleado.id_empleado} value={empleado.id_empleado}>
                        {empleado.pnombre + " " + empleado.apaterno + " " + empleado.amaterno}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
              <div className='form-level-3'>
                <div className='form-group'>
                  <label>Cliente</label>
                  <select name="numrun_cliente" value={formData.numrun_cliente} onChange={handleChange}>
                    {clientes.map((empresa) => (
                      <option key={empresa.id} value={empresa.id}>
                        {empresa.numrun_empresa}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
            </form>
          </div>
        </div>
        <div className='visita-tecnica-crear-footer'>
          <button type="submit" onClick={handleSubmit}>Crear Visita Técnica</button>
        </div>
      </div>
      <div className='visita-tecnica-crear-panel'>
        {formData.fec_programacion_vt && formData.id_empleado ? (
          <div>
            <h1>VISITAS PROGRAMADAS</h1>
            <p>EMPLEADO: {empleado?.pnombre ? `${empleado.pnombre} ${empleado.apaterno || ''} ${empleado.snombre || ''}` : ''} </p>
            <p>FECHA: {formData.fec_programacion_vt} </p>
            {cantVisitas > 0 ? (
              <p>VISITAS AGENDADAS: {cantVisitas}</p>
            ) : (
              <p>El técnico no posee visitas agendadas para este día.</p>
            )}
            {visitasFiltradas.map((visita, index) => (
              <div key={index} className="visita-tecnica" onClick={() => ""}>
                <div>
                  <p>CREADA: {visita.fec_creacion_vt}</p>
                  <h4>VT {visita.id_vt}</h4>
                </div>
                <div>
                  <p>CLIENTE: {visita.CLIENTE.nombre_cliente}</p>
                </div>
                <div>
                  <p>{visita.desc_vt}</p>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div></div>
        )}
      </div>
    </div>
  );
};
