import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './GestionUsuarios.css';

const GestionUsuarios = () => {
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Aquí deberías hacer una llamada a la API para obtener los usuarios
    const fetchedUsers = [
      { id: 1, nombre: 'Juan Pérez', correo: 'juan.perez@example.com', rol: 'Administrador', estado: 'Activo' },
      { id: 1, nombre: 'Juan Pérez', correo: 'juan.perez@example.com', rol: 'Administrador', estado: 'Activo' },
      { id: 1, nombre: 'Juan Pérez', correo: 'juan.perez@example.com', rol: 'Administrador', estado: 'Activo' },
      // Añade más usuarios según sea necesario
    ];
    setUsers(fetchedUsers);
  }, []);

  const handleDelete = (userId) => {
    if (window.confirm("¿Estás seguro de que deseas eliminar este usuario?")) {
      // Lógica para eliminar el usuario de la base de datos
      setUsers(users.filter(user => user.id !== userId));
    }
  };

  return (
    <div className="table-container">
      <header>
        <h1>Administración de Usuarios</h1>
        <button onClick={() => navigate('/agregar-usuario')}>Agregar Usuario</button>
      </header>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Correo Electrónico</th>
            <th>Rol</th>
            <th>Estado</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id} className="table-row" onClick={() => navigate(`/editar-usuario/${user.id}`)}>
              <td>{user.id}</td>
              <td>{user.nombre}</td>
              <td>{user.correo}</td>
              <td>{user.rol}</td>
              <td>{user.estado}</td>
              <td>
                <button onClick={(e) => { e.stopPropagation(); navigate(`/editar-usuario/${user.id}`); }}>Editar</button>
                <button onClick={(e) => { e.stopPropagation(); handleDelete(user.id); }}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default GestionUsuarios;
