import React, { useState } from 'react';
import { Switch, FormControlLabel } from '@mui/material';
import './GestionUsuariosCard.css';
import { cambiarEstadoUsuario } from '../services/GestionUsuariosService'; // Ajusta la ruta según sea necesario

export const GestionUsuariosCard = ({ user, onClose }) => {
    const [estadoUsuario, setEstadoUsuario] = useState(user.estado_usuario);

    const handleEstadoChange = async (event) => {
        const nuevoEstado = event.target.checked;
        const resultado = await cambiarEstadoUsuario(user.id_usuario, nuevoEstado);
        
        if (resultado) {
            setEstadoUsuario(nuevoEstado);
        } else {
            console.error('No se pudo actualizar el estado del usuario');
        }
    };

    return (
        <div className='modal-container' >
            <div className="card">
                <div className="card-header">
                    <span onClick={onClose}>X</span>
                </div>
                <div className="card-title">
                    <h1>Datos del Usuario</h1>
                </div>
                <div className='card-image'>
                    <img src={JSON.parse(localStorage.getItem('usuario')).profile_image || '../img/isotipo-arsheep.png'} alt={`Imagen de`} />
                </div>
                <div className="card-content">
                    <div className="card-row">
                        <div className="card-item">
                            <strong>NOMBRE DE USUARIO:</strong><span>{user.nombre_usuario}</span>
                        </div>
                    </div>
                    <div className="card-row">
                        <div className="card-item">
                            <strong>ID EMPLEADO:</strong><span>{user.id_empleado}</span>
                        </div>
                        <div className="card-item">
                            <strong>FUNCIÓN:</strong><span>{user.EMPLEADO.ROL.desc_rol}</span>
                        </div>
                    </div>
                    <div className="card-row">
                        <div className="card-item">
                            <strong>NOMBRE:</strong><span>{`${user.EMPLEADO.pnombre} ${user.EMPLEADO.apaterno} ${user.EMPLEADO.amaterno}`}</span>
                        </div>
                    </div>
                    <div className="card-row">
                        <div className="card-item">
                            <strong>TELÉFONO:</strong><span>{`${user.EMPLEADO.numero_contacto}`}</span>
                        </div>
                    </div>
                </div>
                <div className="card-footer">
                    <FormControlLabel
                        control={<Switch checked={estadoUsuario} onChange={handleEstadoChange} />}
                        label="Activar Usuario"
                    />
                </div>
            </div>
        </div>
    );
};
