import React, { useEffect, useState } from 'react';
import './PageVisitaTecnica.css'
import { getOrdenesTrabajo } from '../../../services/ordenTrabajoService';
import { getVisitasTecnicas } from '../../../services/visitaTecnicaService';
import { GestionVisitaTecnica } from './GestionVisitaTecnica';


export const PageVisitaTecnica = () => {
  const [dataVisitasTecnicas, setdataVisitasTecnicas] = useState([]);
  const [showPageOTCrear, setShowPageOTCrear] = useState(false);
  const [selectedVisita, setSelectedVisita] = useState(null); // Estado para manejar la orden seleccionada

  const fetchData = async () => {
    try {
      const data = await getVisitasTecnicas();
      setdataVisitasTecnicas(data); // Guardar los datos en el estado
      console.log(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const getColorPrioridad = (prioridad) => {
    switch (prioridad) {
      case 'Baja':
        return 'green';
      case 'Media':
        return 'orange';
      case 'Alta':
        return 'red';
      default:
        return 'white';
    }
  };

  const handleOrdenClick = (orden) => {
    setSelectedVisita(orden); // Establece la orden seleccionada}
    setShowPageOTCrear(false);
    console.log(selectedVisita);
  };

  return (
    <div className='page-visitas-tecnicas-container'>

      <div className='filter'>
        <button onClick={() => setShowPageOTCrear(true) + setSelectedVisita("")}>Agregar</button>
      </div>

      <div className='visitas-tecnicas-container'>
        {dataVisitasTecnicas.map((visita, index) => (
          <div key={index} className="visita-tecnica" onClick={() => handleOrdenClick(visita)}>
            <div>
              <p>CREADA: {visita.fec_creacion_vt}</p>
              <h4>VT {visita.id_vt}</h4>
            </div>
            <p>{visita.desc_vt}</p>
            <div>
              <p>ESTADO: {visita.id_estado_vt}</p>
            </div>

          </div>
        ))}
      </div>
      <div className='visita-tecnica-panel'>
      {selectedVisita && <GestionVisitaTecnica orden={selectedVisita} />} {/* Renderiza el nuevo componente */}
      </div>
    </div>
  );
};
