import React, { useEffect, useState } from 'react';
import "./UserData.css";
import { InfoCard } from '../../InfoCard/InfoCard';

export const UserData = () => {
    const [username, setUsername] = useState('');
    const [nombreEmpleado, setNombreEmpleado] = useState('');
    const [rol, setRol] = useState('');

    const [usuario, setUsuario] = useState('');
    const [tipoUsuario, setTipoUsuario] = useState('');
    const [empleado, setEmpleado] = useState(null);
    const [departamento, setDepartamento] = useState(null);

    const data = {
        RUT:'18.980.582-9',
        NOMBRE: 'ESTEBAN',
        APELLIDOS: 'JARA GONZÁLEZ',
        DIRECCIÓN: 'LOMA BLANCA 2760, QUILPUÉ'
    };

    useEffect(() => {

        const storedUser = JSON.parse(localStorage.getItem('usuario'));
        const storedEmpleado = JSON.parse(localStorage.getItem('empleado'));
        const storedRol = JSON.parse(localStorage.getItem('rol'));
        const storedTipoUsuario = JSON.parse(localStorage.getItem('tipoUsuario'));
        const storedDepartamento = JSON.parse(localStorage.getItem('departamento'));


        if (storedUser) {
            setUsername(storedUser.nombre_usuario);
            setNombreEmpleado(`${storedEmpleado?.pnombre || ''} ${storedEmpleado?.snombre || ''} ${storedEmpleado?.apaterno || ''} ${storedEmpleado?.amaterno || ''}`);
            setRol(storedRol.desc_rol);

            setUsuario(storedUser);
            setTipoUsuario(storedTipoUsuario);
            setEmpleado(storedEmpleado);
            setDepartamento(storedDepartamento);
        }
    }, []);

    return (
        <div className='user-config'>
            <div className='user-config-img'>
                <img src={usuario?.profile_image || '../img/isotipo-arsheep.png'} alt={`Imagen de ${username}`} />
            </div>

            <div className='user-config-data'>
                <h1>Datos del usuario</h1>
                <div className='user-config-data-item'>
                    <label>USUARIO: </label> <p>{usuario?.nombre_usuario}</p>
                </div>
                <div className='user-config-data-item'>
                    <label>TIPO DE USUARIO: </label> <p>{tipoUsuario?.desc_tipo_usuario}</p>
                </div>
            </div>

            {/**empleado && (
                <div className='user-config-data'>
                    <h1>Datos del empleado</h1>
                    <div className='user-config-data-item'>
                        <label>NOMBRE: </label> <p>{nombreEmpleado}</p>
                    </div>
                    <div className='user-config-data-item'>
                        <label>ROL: </label> <p>{rol}</p>
                    </div>
                    <div className='user-config-data-item'>
                        <label>DEPARTAMENTO: </label> <p>{departamento.desc_departamento}</p>
                    </div>
                </div>)*/}
            

            <InfoCard title="Información del Usuario" data={data} />

        </div>
    );
};
