import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './GestionUsuarios.css';
import { obtenerUsuariosEmpleados } from '../../../../services/GestionUsuariosService';

const GestionUsuarios = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [usuarioSeleccionado, setUsuarioSeleccionado] = useState(null);
  const [filtro, setFiltro] = useState("");
  const [criterio, setCriterio] = useState("nombre_usuario");
  const [showModal, setShowModal] = useState(false);
  const [roles, setRoles] = useState({});

  const fetchData = async () => {
    try {
      const usuariosEmpleados = await obtenerUsuariosEmpleados();
      console.log("Clientes obtenidos:", usuariosEmpleados);
      setUsers(usuariosEmpleados || []);
    } catch (error) {
      console.error("Error al obtener usuarios empleados:", error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    const rolesMockup = {
      1: { desc_tipo_usuario: 'Administrador' },
      2: { desc_tipo_usuario: 'Usuario' },
    };
    setRoles(rolesMockup);
  }, []);

  useEffect(() => {
    aplicarFiltro();
  }, [filtro, criterio, users]);

  const seleccionarUsuario = (usuario) => {
    setUsuarioSeleccionado(usuario);
  };

  const handleFilterChange = (e) => {
    setFiltro(e.target.value);
  };

  const handleCriterioChange = (e) => {
    setCriterio(e.target.value);
  };

  const aplicarFiltro = () => {
    const filtered = users.filter((usuario) =>
      usuario[criterio]?.toString().toLowerCase().includes(filtro.toLowerCase())
    );
    setFilteredUsers(filtered);
  };

  const deshacerFiltro = () => {
    setFilteredUsers(users);
    setFiltro("");
  };

  const handleShowModal = () => {
    setShowModal(true);
    setUsuarioSeleccionado(null);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleCloseDashboard = () => {
    setUsuarioSeleccionado(null);
  };

  const handleEliminarUsuario = (id) => {
    const confirmacion = window.confirm("¿Estás seguro de que deseas eliminar este usuario?");
    if (confirmacion) {
      setUsers(users.filter(usuario => usuario.id_usuario !== id));
      deshacerFiltro();
      setUsuarioSeleccionado(null);
    }
  };

  const parsearFecha = (fecha) => {
    const [año, mes, dia] = fecha.split('-');
    return `${dia}-${mes}-${año}`;
  };

  return (
    <div className="principal-container">
      <div className="secondary-container">
        <div className="filter-container">
          <div className="filter-container-header">
            <p>Filtrar</p>
          </div>
          <div className="filter-container-group">
            <select value={criterio} onChange={handleCriterioChange}>
              <option value="id_usuario">ID</option>
              <option value="nombre_usuario">Nombre</option>
              <option value="numrun_cliente">RUT</option>
              <option value="id_empleado">ID Empleado</option>
              <option value="id_tipo_usuario">Tipo de Usuario</option>
              <option value="fec_creacion_usuario">Fecha de Creación</option>
            </select>
            <input
              type="text"
              value={filtro}
              onChange={handleFilterChange}
              placeholder="Ingrese filtro..."
            />
          </div>
          <div className="filter-container-button">
            <button onClick={aplicarFiltro}>Filtrar</button>
            <button onClick={deshacerFiltro}>Deshacer</button>
          </div>
        </div>
        <div className="control-buttons-container">
          <div className="control-buttons-container-group">
            <button onClick={handleShowModal}>Agregar Usuario</button>
            <button>PDF</button>
            <button>Excel</button>
          </div>
        </div>
        <div className="table-container">
          <table className="table">
            <thead>
              <tr>
                <th>ID Usuario</th>
                <th>Nombre de Usuario</th>
                <th>ID Empleado</th>
                <th>Nombre</th>
                <th>Función</th>
                <th>Estado</th>
                <th>Fecha de Creación</th>
              </tr>
            </thead>
            <tbody>
              {filteredUsers.map((usuarioEmpleado) => (
                <tr
                  key={usuarioEmpleado.id_usuario}
                  onClick={() => seleccionarUsuario(usuarioEmpleado)}
                  className="table-row"
                >
                  <td>{usuarioEmpleado.id_usuario}</td>
                  <td>{usuarioEmpleado.nombre_usuario}</td>
                  <td>{usuarioEmpleado.id_empleado}</td>
                  <td>{`${usuarioEmpleado.EMPLEADO.pnombre} ${usuarioEmpleado.EMPLEADO.snombre}
                   ${usuarioEmpleado.EMPLEADO.apaterno} ${usuarioEmpleado.EMPLEADO.amaterno}`}</td>
                  <td>{usuarioEmpleado.EMPLEADO.ROL.desc_rol}</td>
                  <td>{usuarioEmpleado.estado_usuario ? 'ACTIVO' : 'INACTIVO'}</td>
                  <td>{parsearFecha(usuarioEmpleado.fec_creacion_usuario)}</td>
             
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default GestionUsuarios;
