import React from 'react';
import { useLocation } from 'react-router-dom';

export const ClienteFicha = () => {
    const location = useLocation();
    const { cliente } = location.state || {};

    return (
        <div className='principal-container'>
            <div className='secondary-container'>
                <div className='container-header'>
                    <h1>Ficha del Cliente</h1>
                </div>
                <div className='secondary-container'>
                    {cliente ? (
                        <div>
                            <p>ID del Cliente: {cliente.numrun_cliente}</p>
                            <p>Nombre: {cliente.pnombre_cliente} {cliente.snombre_cliente} {cliente.appaterno_cliente} {cliente.apmaterno_cliente}</p>
                            <p>Dirección: {cliente.dirección_cliente}</p>
                            <p>Email: {cliente.email_cliente}</p>
                            <p>Teléfono: {cliente.numtelefono_cliente}</p>
                            <p>Fecha de Nacimiento: {new Date(cliente.fecnac_cliente).toLocaleDateString()}</p>
                            <p>Comuna: {cliente.COMUNA.desc_comuna}</p>
                            <p>Región: {cliente.COMUNA.id_region}</p>
                            <p>Tipo de Cliente: {cliente.TIPO_CLIENTE.desc_tipo_cliente}</p>
                            <p>Fecha de Inicio de Contrato: {new Date(cliente.fec_inicio_contrato_cliente).toLocaleDateString()}</p>
                            <p>Fecha de Término de Contrato: {new Date(cliente.fec_termino_contrato_cliente).toLocaleDateString()}</p>
                            <p>Fecha de Creación del Cliente: {new Date(cliente.fec_creacion_cliente).toLocaleDateString()}</p>
                            {cliente.imagen_cliente && (
                                <div>
                                    <img src={cliente.imagen_cliente} alt="Imagen del Cliente" />
                                </div>
                            )}
                        </div>
                    ) : (
                        <p>No se ha seleccionado ningún cliente.</p>
                    )}
                </div>
            </div>
        </div>
    )
}
