import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import './ClienteFicha.css';

export const ClienteFicha = () => {
    const location = useLocation();
    const { cliente } = location.state || {};
    const [rut, setRut] = useState('');

    const formatRUT = (input) => {
        let value = input.replace(/\D/g, ''); // Eliminar caracteres no numéricos
        if (value.length > 1) {
            value = value.slice(0, -1) + '-' + value.slice(-1); // Agregar guion
        }
        if (value.length > 5) {
            value = value.slice(0, -5) + '.' + value.slice(-5); // Agregar primer punto
        }
        if (value.length > 9) {
            value = value.slice(0, -9) + '.' + value.slice(-9); // Agregar segundo punto
        }
        setRut(value);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const [numrun_cliente, dvrun_cliente] = rut.split('-');
        const formattedNumrun = numrun_cliente.replace(/\./g, '');
        // Aquí puedes enviar `formattedNumrun` y `dvrun_cliente` a tu backend o guardarlos en tu estado
        console.log(`numrun_cliente: ${formattedNumrun}, dvrun_cliente: ${dvrun_cliente}`);
    };

    return (
        <div className='principal-container'>
            <div className='secondary-container'>
                <div className='container-header'>
                    <h1>Ficha del Cliente</h1>
                </div>
                <div className='form-container'>
                    {cliente ? (
                        <form onSubmit={handleSubmit}>
                            <div className="form-group">
                                <label>ID del Cliente</label>
                                <input type="text" value={cliente.dvrun_cliente} readOnly />
                            </div>
                            <div className="form-group">
                                <label>Nombre</label>
                                <input type="text" value={`${cliente.pnombre_cliente} ${cliente.snombre_cliente} ${cliente.appaterno_cliente} ${cliente.apmaterno_cliente}`} readOnly />
                            </div>
                            <div className="form-group">
                                <label>Dirección</label>
                                <input type="text" value={cliente.dirección_cliente} readOnly />
                            </div>
                            <div className="form-group">
                                <label>Email</label>
                                <input type="text" value={cliente.email_cliente} readOnly />
                            </div>
                            <div className="form-group">
                                <label>Teléfono</label>
                                <input type="text" value={cliente.numtelefono_cliente} readOnly />
                            </div>
                            <div className="form-group">
                                <label>Fecha de Nacimiento</label>
                                <input type="text" value={new Date(cliente.fecnac_cliente).toLocaleDateString()} readOnly />
                            </div>
                            <div className="form-group">
                                <label>Comuna</label>
                                <input type="text" value={cliente.COMUNA.desc_comuna} readOnly />
                            </div>
                            <div className="form-group">
                                <label>Región</label>
                                <input type="text" value={cliente.COMUNA.id_region} readOnly />
                            </div>
                            <div className="form-group">
                                <label>Tipo de Cliente</label>
                                <input type="text" value={cliente.TIPO_CLIENTE.desc_tipo_cliente} readOnly />
                            </div>
                            <div className="form-group">
                                <label>Fecha de Inicio de Contrato</label>
                                <input type="text" value={new Date(cliente.fec_inicio_contrato_cliente).toLocaleDateString()} readOnly />
                            </div>
                            <div className="form-group">
                                <label>Fecha de Término de Contrato</label>
                                <input type="text" value={new Date(cliente.fec_termino_contrato_cliente).toLocaleDateString()} readOnly />
                            </div>
                            <div className="form-group">
                                <label>Fecha de Creación del Cliente</label>
                                <input type="text" value={new Date(cliente.fec_creacion_cliente).toLocaleDateString()} readOnly />
                            </div>
                            {cliente.imagen_cliente && (
                                <div className="form-group">
                                    <label>Imagen del Cliente</label>
                                    <img src={cliente.imagen_cliente} alt="Imagen del Cliente" className="cliente-imagen" />
                                </div>
                            )}
                            <div className="form-group">
                                <label>RUT</label>
                                <input 
                                    type="text" 
                                    value={rut} 
                                    onChange={(e) => formatRUT(e.target.value)} 
                                    required 
                                />
                            </div>
                            <button type="submit">Guardar RUT</button>
                        </form>
                    ) : (
                        <p>No se ha seleccionado ningún cliente.</p>
                    )}
                </div>
            </div>
        </div>
    );
};
