import React, { useEffect, useState } from 'react';
import { obtenerEmpleadoPorId } from '../../services/supa';

const Empleado = () => {
    const [idEmpleado] = useState(1); // Aquí puedes establecer el ID del empleado que deseas obtener
    const [nombreEmpleado, setNombreEmpleado] = useState({});

    useEffect(() => {
        const fetchEmpleado = async () => {
            const nombre = await obtenerEmpleadoPorId(idEmpleado);
            setNombreEmpleado(nombre);
        };

        fetchEmpleado();
    }, [idEmpleado]);

    return (
        <div>
            <h1>Información del Empleado</h1>
            <p><strong>Primer Nombre:</strong> {nombreEmpleado.pnombre}</p>
            <p><strong>Segundo Nombre:</strong> {nombreEmpleado.snombre}</p>
            <p><strong>Apellido Paterno:</strong> {nombreEmpleado.apaterno}</p>
            <p><strong>Apellido Materno:</strong> {nombreEmpleado.amaterno}</p>
            {/* Otro contenido del componente Empleado */}
        </div>
    );
}

export default Empleado;
