import React, { useEffect, useState } from 'react';
import './PageOrdenesTrabajo.css';
import { getOrdenesTrabajo } from '../../../services/ordenTrabajoService';
import GestionUsuarios from '../gestiones/users/GestionUsuarios';


export const PageOrdenesTrabajo = () => {
  const [dataOrdenesTrabajo, setDataOrdenesTrabajo] = useState([]);
  const [showPageCliente, setShowPageCliente] = useState(false);

  const fetchData = async () => {
    try {
      const data = await getOrdenesTrabajo();
      setDataOrdenesTrabajo(data); // Guardar los datos en el estado
      console.log(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const getColorPrioridad = (prioridad) => {
    switch (prioridad) {
      case 'Baja':
        return 'green';
      case 'Media':
        return 'orange';
      case 'Alta':
        return 'red';
      default:
        return 'white';
    }
  };

  return (
    <div className='page-ordenes-trabajo-container'>

      <div className='filter'>
      <button onClick={() => setShowPageCliente(true)}>Agregar</button>
      </div>

      {showPageCliente && <GestionUsuarios/>}
      <div className='ordenes-trabajo-container'>
        {dataOrdenesTrabajo.map((orden, index) => (
          <div key={index} className="orden-trabajo">
            <h4>OT {orden.id_ot}</h4>
            <p>{orden.descripcion.toUpperCase()}</p>
            <p>{orden.status}</p>
            <p>{orden.fecha_creacion}</p>
            <p>{orden.fecha_vencimiento}</p>
            <p style={{ color: getColorPrioridad(orden.prioridad) }}>{orden.prioridad}</p>
          </div>
        ))}
      </div>
      <div className='ordenes-trabajo-panel'>
        {/* Contenido adicional */}
      </div>
     
    </div>
  );
};
