// inputHelpers.js
const regexMap = {
    number: /^[0-9]$/,
    uppercase: /^[A-Z]$/,
    string: /^.*$/, // Permitir cualquier carácter
  };
  
  export const handleKeyDown = (e, charType) => {
    const regex = regexMap[charType] || /.*/;
    const char = String.fromCharCode(e.charCode);
    if (!regex.test(char)) {
      e.preDefault();
    }
  };
  
  export const handleChange = (e, setValue) => {
    setValue(e.target.value);
  };
