import React, { useEffect, useState } from 'react';
import './GestionVisitaTecnica.css';
import { updateVisitaTecnica, getVisitaById } from '../../../services/visitaTecnicaService'; // Asegúrate de tener un servicio para obtener la visita por ID

export const GestionVisitaTecnica = ({ visita }) => {
    const [desc_vt, setDescVt] = useState(visita.desc_vt || '');
    const [desc_problema_vt, setDescProblemaVt] = useState(visita.desc_problema_vt || '');
    const [analisis_vt, setAnalisisVt] = useState(visita.analisis_vt || '');
    const [recomendacion_vt, setRecomendacionVt] = useState(visita.recomendacion_vt || '');
    const [beneficio_vt, setBeneficioVt] = useState(visita.beneficio_vt || '');
    const [proximospasos_vt, setProximosPasosVt] = useState(visita.proximospasos_vt || '');
    const [fec_creacion_vt, setFecCreacionVt] = useState(visita.fec_creacion_vt || '');
    const [numrun_cliente, setNumrunCliente] = useState(visita.numrun_cliente || '');
    const [id_empleado, setIdEmpleado] = useState(visita.id_empleado || '');
    const [id_estado_vt, setIdEstadoVt] = useState(visita.id_estado_vt || '');
    const [isSubmitting, setIsSubmitting] = useState(false); // Estado para gestionar el envío

    const actualizarVisitaTecnica = async (id, data) => {
        try {
            await updateVisitaTecnica(id, data);
            // Recargar los datos de la visita después de la actualización
            const updatedVisita = await getVisitaById(id);
            // Actualizar los estados con los nuevos datos
            setDescVt(updatedVisita.desc_vt);
            setDescProblemaVt(updatedVisita.desc_problema_vt);
            setAnalisisVt(updatedVisita.analisis_vt);
            setRecomendacionVt(updatedVisita.recomendacion_vt);
            setBeneficioVt(updatedVisita.beneficio_vt);
            setProximosPasosVt(updatedVisita.proximospasos_vt);
            setFecCreacionVt(updatedVisita.fec_creacion_vt);
            setNumrunCliente(updatedVisita.numrun_cliente);
            setIdEmpleado(updatedVisita.id_empleado);
            setIdEstadoVt(updatedVisita.id_estado_vt);

        } catch (error) {
            console.error('Error updating visita:', error);
        } finally {
            setIsSubmitting(false); // Restablecer el estado de envío
        }
    };

    const handleSave = (e) => {
        e.preventDefault(); // Prevenir el comportamiento predeterminado del formulario
        setIsSubmitting(true); // Indicar que se está enviando
        const updatedVisita = {
            desc_vt,
            desc_problema_vt,
            analisis_vt,
            recomendacion_vt,
            beneficio_vt,
            proximospasos_vt,
            fec_creacion_vt,
            numrun_cliente,
            id_empleado,
            id_estado_vt
        };
        actualizarVisitaTecnica(visita.id_vt, updatedVisita);
    };

    useEffect(() => {
        // Opcionalmente, podrías usar este efecto para recargar los datos iniciales
        const cargarDatosVisita = async () => {
            const updatedVisita = await getVisitaById(visita.id_vt);
            setDescVt(updatedVisita.desc_vt);
            setDescProblemaVt(updatedVisita.desc_problema_vt);
            setAnalisisVt(updatedVisita.analisis_vt);
            setRecomendacionVt(updatedVisita.recomendacion_vt);
            setBeneficioVt(updatedVisita.beneficio_vt);
            setProximosPasosVt(updatedVisita.proximospasos_vt);
            setFecCreacionVt(updatedVisita.fec_creacion_vt);
            setNumrunCliente(updatedVisita.numrun_cliente);
            setIdEmpleado(updatedVisita.id_empleado);
            setIdEstadoVt(updatedVisita.id_estado_vt);
        };

        cargarDatosVisita();
    }, []);

    return (
        <div className='visita-tecnica-gestion-container'>
            <div className='visita-tecnica-gestion-header'>
                <h2>ACTUALIZAR VISITA # {visita.ESTABLECIMIENTO.id_establecimiento} DEL {visita.fec_creacion_vt}</h2>
            </div>
            <div className='visita-tecnica-gestion-content'>
                <div className='visita-tecnica-gestion-form-container'>
                    <form onSubmit={handleSave}>
                        <div className='form-level-1'>
                            <div className='form-group'>
                                <label>DESCRIPCIÓN</label>
                                <input
                                    type='text'
                                    value={desc_vt}
                                    onChange={(e) => setDescVt(e.target.value)}
                                />
                                <label>PROBLEMA</label>
                                <textarea
                                    value={desc_problema_vt}
                                    onChange={(e) => setDescProblemaVt(e.target.value)}
                                ></textarea>
                                <label>ANALISIS</label>
                                <textarea
                                    value={analisis_vt}
                                    onChange={(e) => setAnalisisVt(e.target.value)}
                                ></textarea>
                                <label>RECOMENDACIONES</label>
                                <textarea
                                    value={recomendacion_vt}
                                    onChange={(e) => setRecomendacionVt(e.target.value)}
                                ></textarea>
                                <label>BENEFICIOS</label>
                                <textarea
                                    value={beneficio_vt}
                                    onChange={(e) => setBeneficioVt(e.target.value)}
                                ></textarea>
                            </div>
                        </div>
                        <div className='form-level-2'>
                            {/* Otros campos del formulario */}
                        </div>
                        <div className='form-level-4'>
                            <button type='submit' disabled={isSubmitting}>
                                {isSubmitting ? 'Guardando...' : 'Guardar'}
                            </button>
                        </div>
                    </form>
                </div>
            </div>
            <div className='visita-tecnica-gestion-footer'>
                <button>ACTUALIZAR</button>
                <button>GENERAR O.T.</button>
            </div>
        </div>
    );
};

const getColorPrioridad = (prioridad) => {
    switch (prioridad) {
        case 'Baja':
            return 'green';
        case 'Media':
            return 'orange';
        case 'Alta':
            return 'red';
        default:
            return 'white';
    }
};
