import React, { useEffect, useState } from 'react';
import "./UserInfo.css";

export const UserInfo = () => {
    const [username, setUsername] = useState('');
    const [nombreEmpleado, setNombreEmpleado] = useState('');
    const [descTipoDeUsuario, setDescTipoDeUsuario] = useState('');

    useEffect(() => {
   
        const storedUser = JSON.parse(localStorage.getItem('usuario'));
        const storedEmpleado = JSON.parse(localStorage.getItem('empleado'));
        const storedTipoUsuario = JSON.parse(localStorage.getItem('tipoDeUsuario'));

        if (storedUser) {
            setNombreEmpleado(storedEmpleado.pnombre+" "+storedEmpleado.apaterno+" "+storedEmpleado.amaterno); // Asume que el nombre de usuario está en la propiedad nombre_usuario
        }

    }, []);

    return (
        <div className='user-info'>
            <div className='user-info-img'>
                <img src="../img/isotipo-arsheep.png" alt="Descripción" />
            </div>
            <p>{nombreEmpleado}</p>
            <p>Cargo del usuario</p>
        </div>
    )
}
