import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { obtenerUsuario, obtenerOrdenesDeTrabajo } from '../../services/supa';
import { useUser } from '../usuario/UsuarioContexto';
import "./Login.css";

export const Login = () => {

    const [data, setData] = useState([]);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const navigate = useNavigate();
    const { setUser } = useUser();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const usuarios = await obtenerUsuario();
                console.log("Usuarios cargados:", usuarios);
                setData(usuarios);

                console.log("Usuario y contraseña:", username, password);
                const user = usuarios.find(usuario => usuario.nombre_usuario === username && usuario.contrasenha === password);
                console.log("Usuario encontrado:", user);

                if (user) {
                    setUser(user);
                    const ordenes = await obtenerOrdenesDeTrabajo();
                    console.log("Órdenes de trabajo cargadas:", ordenes);
                    if (ordenes) {
                        const filteredOrdenes = ordenes.filter(ot => ot.run_cliente === user.rut);
                        console.log("Órdenes filtradas:", filteredOrdenes);
                        setData(filteredOrdenes);
                    }
                }
            } catch (error) {
                console.error("Error al cargar los datos:", error);
            }
        };
        fetchData();
    }, [username, password, setUser]);

    const handleSubmit = (e) => {
        e.preventDefault();

        console.log("Datos enviados al submit:", username, password);
        const usuario = data.find(usuario => usuario.nombre_usuario === username && usuario.contrasenha === password);
        console.log("Usuario ingresado:", usuario);
        if (usuario) {
            setUser({ nombre_usuario: usuario.nombre_usuario, rut: usuario.rut, tipo_usuario: usuario.id_tipo_usuario }); 
            navigate('/Layout')
        } else {
            alert('Usuario o contraseña incorrectos');
        }
    };

    return (
        <div className='login-container'>
            <form onSubmit={handleSubmit} className='form-container'>
                <div className='form-group'>
                    <label htmlFor="username">Usuario:</label>
                    <input
                        id="username"
                        type="text"
                        value={username}
                        onChange={e => setUsername(e.target.value)}
                    />
                </div>

                <div className='form-group'>
                    <label htmlFor="password">Contraseña:</label>
                    <input
                        id="password"
                        type="password"
                        value={password}
                        onChange={e => setPassword(e.target.value)}
                    />
                </div>

                <button type="submit">Iniciar sesión</button>
            </form>
        </div>
    );
};
