import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { obtenerUsuario } from '../../services/supa';
import { useUser } from '../usuario/UsuarioContexto';
import "./Login.css";

export const Login = () => {

    const [data, setData] = useState([]);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const navigate = useNavigate();
    const { setUser } = useUser();

    //let iniciarSesion = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            const usuarios = await obtenerUsuario();
            setData(usuarios);
        };
        fetchData();
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();

        // Busca en data si existe un usuario con el username y password ingresados
        const usuario = data.find(usuario => usuario.nombre_usuario === username && usuario.contrasenha === password);
        if (usuario) {
            setUser({ nombre_usuario: usuario.nombre_usuario, rut: usuario.rut, tipo_usuario: usuario.id_tipo_usuario }); 
            navigate('/Layout')
        } else {
            alert('Usuario o contrase침a incorrectos'); // O opcionalmente manejar este caso de otra manera
        }

    };

    return (
     

            <div className='login-container'>
                <form onSubmit={handleSubmit} className='form-container'>
                    <div className='form-group'>
                        <label htmlFor="username">Usuario:</label>
                        <input
                            id="username"
                            type="text"
                            value={username}
                            onChange={e => setUsername(e.target.value)}
                        />
                    </div>

                    <div className='form-group'>
                        <label htmlFor="password">Contrase침a:</label>
                        <input
                            id="password"
                            type="password"
                            value={password}
                            onChange={e => setPassword(e.target.value)}
                        />
                    </div>

                    <button type="submit">Iniciar sesi칩n</button>
                </form>
            </div>

    );

}
