import React, { useState } from 'react';
import { Switch, FormControlLabel } from '@mui/material';
import './GestionUsuariosCard.css';

export const GestionUsuariosCard = ({ user }) => {
  const [visible, setVisible] = useState(true);

  const handleVisible = (e) => {
    setVisible(false);
  };

  return (
    visible && (
      <div className='modal-container'>
        <div className="card">
          <div className="card-header">
            <span onClick={handleVisible}>X</span>
          </div>

          <div className="card-title">
            <h1>Datos del Empleado</h1>
          </div>
          <div className="card-content">
            <div className="card-row">
              <div className="card-item">
                <strong>ID USUARIO:</strong><span>{user.id_usuario}</span>
              </div>
              <div className="card-item">
                <strong>NOMBRE DE USUARIO:</strong><span>{user.nombre_usuario}</span>
              </div>
            </div>
            <div className="card-row">
              <div className="card-item">
                <strong>ID EMPLEADO:</strong><span>{user.id_empleado}</span>
              </div>
              <div className="card-item">
                <strong>FUNCIÃ“N:</strong><span>{user.EMPLEADO.ROL.desc_rol}</span>
              </div>
            </div>
            <div className="card-row">
              <div className="card-item">
                <strong>NOMBRE:</strong><span>{`${user.EMPLEADO.pnombre} ${user.EMPLEADO.apaterno} ${user.EMPLEADO.amaterno}`}</span>
              </div>
            </div>
          </div>
          <div className="card-footer">
            <FormControlLabel
              control={<Switch name="checkedA" checked={user.estado_usuario} />}
              label="Activar Usuario"
            />
          </div>
        </div>
      </div>
    )
  );
};
