import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './ClienteFicha.css';
import { subirImagenUsuarioEmpleado } from '../../../services/GestionUsuariosService';
import { getComunasByRegion, getRegiones } from '../../../services/RegionComunaService';
import { ClienteTile } from './ClienteTile'; // Asegúrate de importar el componente ClienteTile

export const ClienteFicha = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const { cliente } = location.state || {};
    const [selectedImage, setSelectedImage] = useState(cliente?.profile_image || '../../img/no_photo_arsheep.png');
    const [fileImage, setFileImage] = useState();
    const [rut, setRut] = useState('');
    const [firstName, setFirstName] = useState(cliente ? cliente.nombre_cliente : '');
    const [lastName, setLastName] = useState(cliente ? cliente.appaterno_cliente : '');
    const [secondLastName, setSecondLastName] = useState(cliente ? cliente.apmaterno_cliente : '');
    const [birthDate, setBirthDate] = useState(cliente ? cliente.fecnac_cliente : '');
    const [address, setAddress] = useState(cliente ? cliente.dirección_cliente : '');
    const [comuna, setComuna] = useState(cliente ? cliente.COMUNA.desc_comuna : '');
    const [region, setRegion] = useState(cliente ? cliente.COMUNA.id_region : '');
    const [email, setEmail] = useState(cliente ? cliente.email_cliente : '');
    const [phone, setPhone] = useState(cliente ? cliente.numtelefono_cliente : '');
    const [clientType, setClientType] = useState(cliente ? cliente.TIPO_CLIENTE.desc_tipo_cliente : '');
    const [startDate, setStartDate] = useState(cliente ? cliente.fec_inicio_contrato_cliente : '');
    const [endDate, setEndDate] = useState(cliente ? cliente.fec_termino_contrato_cliente : '');

    const [regionesList, setRegionesList] = useState([]);
    const [comunasList, setComunasList] = useState([]);

    const formatRUT = (input) => {
        let value = input.replace(/[^0-9kK]/g, '');
        value = value.toUpperCase();
        if (value.includes('K') && value.indexOf('K') !== value.length - 1) {
            value = value.replace(/K/g, '');
        }
        if (value.length > 9) {
            value = value.slice(0, 8) + value.slice(8, 9);
        }
        if (value.length > 1) {
            value = value.slice(0, -1) + '-' + value.slice(-1);
        }
        if (value.length > 5) {
            value = value.slice(0, -5) + '.' + value.slice(-5);
        }
        if (value.length > 9) {
            value = value.slice(0, -9) + '.' + value.slice(-9);
        }
        if (value.length > 12) {
            value = value.slice(0, 12);
        }
        setRut(value);
    };

    useEffect(() => {
        const fetchRegiones = async () => {
            try {
                const regiones = await getRegiones();
                setRegionesList(regiones);
            } catch (error) {
                console.error('Error al obtener las regiones:', error);
            }
        };
        fetchRegiones();
    }, []);

    useEffect(() => {
        const fetchComunas = async () => {
            try {
                const comunas = await getComunasByRegion(region);
                setComunasList(comunas);
            } catch (error) {
                console.error('Error al obtener las comunas:', error);
            }
        };
        if (region) {
            fetchComunas();
        }
    }, [region]);

    const formatInputText = (input, maxLength) => {
        let formattedName = input.toUpperCase();
        formattedName = formattedName.replace(/[a-z]/g, '');
        formattedName = formattedName.slice(0, maxLength);
        return formattedName;
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const [numrun_cliente, dvrun_cliente] = rut.split('-');
        const formattedNumrun = numrun_cliente.replace(/\./g, '');
        const clienteData = {
            numrun_cliente: formattedNumrun,
            dvrun_cliente: dvrun_cliente,
            pnombre_cliente: firstName,
            appaterno_cliente: lastName,
            apmaterno_cliente: secondLastName,
            fecnac_cliente: birthDate,
            dirección_cliente: address,
            id_comuna: comuna,
            id_region: region,
            email_cliente: email,
            numtelefono_cliente: phone,
            desc_tipo_cliente: clientType,
            fec_inicio_contrato_cliente: startDate,
            fec_termino_contrato_cliente: endDate
        };
        console.log('Cliente Data:', clienteData);
    };

    const handleImageUpload = async (file) => {
        if (!file) return;
        const formData = new FormData();
        formData.append('image', file);
        formData.append('id_usuario', cliente.id_usuario);
        formData.append('imagen_antigua', cliente.profile_image);

        try {
            const response = await subirImagenUsuarioEmpleado(formData);
            if (response && response.newImageUrl) {
                setSelectedImage(response.newImageUrl);
            } else {
                console.error('La respuesta del servidor no contiene la URL de la imagen');
            }
        } catch (error) {
            console.error('Error al subir la imagen:', error);
        }
    };

    const handleImageChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const validImageTypes = ['image/jpeg', 'image/png', 'image/gif'];
            if (!validImageTypes.includes(file.type)) {
                alert("Solo se permiten archivos de imagen (jpeg, png, gif).");
                return;
            }
            const reader = new FileReader();
            reader.onloadend = () => {
                setSelectedImage(reader.result);
                setFileImage(file);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleTileClick = (cliente) => {
        navigate('/ruta-a-ficha-del-cliente', { state: { cliente } });
    };

    return (
        <div className='principal-container'>
            <div className='secondary-container'>
                <div className='container-header'>
                    <h1>Ficha del Cliente</h1>
                </div>
                <div className='form-container'>
                    {cliente ? (
                        <form onSubmit={handleSubmit}>
                            <div className='card-image'>
                                <img
                                    src={selectedImage}
                                    alt={`Imagen de ${cliente.nombre_usuario}`}
                                    onClick={() => document.getElementById('imageUpload').click()}
                                />
                                <input
                                    type="file"
                                    id="imageUpload"
                                    style={{ display: 'none' }}
                                    onChange={handleImageChange}
                                />
                            </div>
                            <ClienteTile cliente={cliente} onTileClick={handleTileClick} /> {/* Utiliza el componente ClienteTile */}
                            <div className='form-level'>
                                <div className='form-group'>
                                    <label htmlFor="rut" className="label-rut">RUT</label>
                                    <input
                                        id="rut"
                                        className="input-rut"
                                        type="text"
                                        value={rut}
                                        onChange={(e) => formatRUT(e.target.value)}
                                        required
                                    />
                                </div>
                            </div>
                                <div className='form-level'>
                                    <div className='form-group'>
                                        <label htmlFor="firstName" className="label-name">NOMBRE</label>
                                        <input
                                            id="firstName"
                                            className="input-name"
                                            type="text"
                                            value={firstName}
                                            onChange={(e) => setFirstName(formatInputText(e.target.value, 30))}
                                        />
                                    </div>
                                </div>
                                <div className='form-level'>
                                    <div className='form-group'>
                                        <label htmlFor="lastName" className="label-name">APELLIDO</label>
                                        <input
                                            id="lastName"
                                            className="input-name"
                                            type="text"
                                            value={lastName}
                                            onChange={(e) => setLastName(formatInputText(e.target.value, 30))}
                                        />
                                    </div>
                                </div>
                                <div className='form-level'>
                                    <div className='form-group'>
                                        <label htmlFor="secondLastName" className="label-name">SEGUNDO APELLIDO</label>
                                        <input
                                            id="secondLastName"
                                            className="input-name"
                                            type="text"
                                            value={secondLastName}
                                            onChange={(e) => setSecondLastName(formatInputText(e.target.value, 30))}
                                        />
                                    </div>
                                </div>
                                <div className='form-level'>
                                    <div className='form-group'>
                                        <label htmlFor="birthDate" className="label-birthDate">Fecha de Nacimiento</label>
                                        <input
                                            id="birthDate"
                                            className="input-birthDate"
                                            type="date"
                                            value={birthDate}
                                            onChange={(e) => setBirthDate(e.target.value)}
                                        />
                                    </div>
                                </div>
                                <div className='form-level'>
                                    <div className='form-group'>
                                        <label htmlFor="address" className="label-address">DIRECCIÓN</label>
                                        <input
                                            id="address"
                                            className="input-address"
                                            type="text"
                                            value={address}
                                            onChange={(e) => setAddress(formatInputText(e.target.value, 60))}
                                        />
                                    </div>
                                </div>
                                <div className='form-level'>
                                    <div className='form-group'>
                                        <label htmlFor="region" className="label-region">REGIÓN</label>
                                        <select
                                            id="region"
                                            className="input-region"
                                            value={region}
                                            onChange={(e) => setRegion(e.target.value)}
                                        >
                                            <option value="">Seleccione una región</option>
                                            {regionesList.map((region) => (
                                                <option key={region.id_region} value={region.id_region}>
                                                    {region.desc_region}
                                                </option>
                                            ))}
                                        </select>
                                    </div>
                                </div>
                                <div className='form-level'>
                                    <div className='form-group'>
                                        <label htmlFor="comuna" className="label-comuna">COMUNA</label>
                                        <select
                                            id="comuna"
                                            className="input-comuna"
                                            value={comuna}
                                            onChange={(e) => setComuna(e.target.value)}
                                        >
                                            <option value="">Seleccione una comuna</option>
                                            {comunasList.map((comuna) => (
                                                <option key={comuna.id_comuna} value={comuna.id_comuna}>
                                                    {comuna.desc_comuna}
                                                </option>
                                            ))}
                                        </select>
                                    </div>
                                </div>
                                <div className='form-level'>
                                    <div className='form-group'>
                                        <label htmlFor="email" className="label-email">EMAIL</label>
                                        <input
                                            id="email"
                                            className="input-email"
                                            type="email"
                                            value={email}
                                            onChange={(e) => setEmail(e.target.value)}
                                        />
                                    </div>
                                </div>
                                <div className='form-level'>
                                    <div className='form-group'>
                                        <label htmlFor="phone" className="label-phone">TELÉFONO</label>
                                        <input
                                            id="phone"
                                            className="input-phone"
                                            type="text"
                                            value={phone}
                                            onChange={(e) => setPhone(e.target.value)}
                                        />
                                    </div>
                                </div>
                                <div className='form-level'>
                                    <div className='form-group'>
                                        <label htmlFor="clientType" className="label-clientType">TIPO DE CLIENTE</label>
                                        <input
                                            id="clientType"
                                            className="input-clientType"
                                            type="text"
                                            value={clientType}
                                            onChange={(e) => setClientType(e.target.value)}
                                        />
                                    </div>
                                </div>
                                <div className='form-level'>
                                    <div className='form-group'>
                                        <label htmlFor="startDate" className="label-startDate">INICIO DE CONTRATO</label>
                                        <input
                                            id="startDate"
                                            className="input-startDate"
                                            type="date"
                                            value={startDate}
                                            onChange={(e) => setStartDate(e.target.value)}
                                        />
                                    </div>
                                </div>
                                <div className='form-level'>
                                    <div className='form-group'>
                                        <label htmlFor="endDate" className="label-endDate">TÉRMINO DE CONTRATO</label>
                                        <input
                                            id="endDate"
                                            className="input-endDate"
                                            type="date"
                                            value={endDate}
                                            onChange={(e) => setEndDate(e.target.value)}
                                        />
                                    </div>
                                </div>
                                <button type="submit" className="submit">Guardar Datos</button>
                        </form>
                    ) : (
                        <p>No se ha seleccionado ningún cliente.</p>
                    )}
                </div>
            </div>
        </div>
    );
};
