import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './GestionUsuarios.css';
import { obtenerUsuarios, eliminarUsuario } from '../../services/UsuarioService';
import { obtenerRoles } from '../../services/RolService';
import { FormAgregarUsuario } from './FormAgregarUsuario';
import { DashboardDetailsUsuario } from './DashboardDetailsUsuario';

const GestionUsuarios = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [usuarioSeleccionado, setUsuarioSeleccionado] = useState(null);
  const [filtro, setFiltro] = useState("");
  const [criterio, setCriterio] = useState("nombre_usuario");
  const [showModal, setShowModal] = useState(false);
  const [roles, setRoles] = useState({});

  const fetchRoles = async (usuarios) => {
    const fetchedRoles = {};
    for (const usuario of usuarios) {
      fetchedRoles[usuario.id_tipo_usuario] = await obtenerRoles(usuario.id_tipo_usuario);
    }
    setRoles(fetchedRoles);
  };

  const fetchData = async () => {
    try {
      const usuarios = await obtenerUsuarios();
      setUsers(usuarios || []);
      setFilteredUsers(usuarios || []);
      await fetchRoles(usuarios || []);
    } catch (error) {
      console.error("Error al obtener usuarios:", error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    setFilteredUsers(users);
  }, [users]);

  const seleccionarUsuario = (usuario) => {
    setUsuarioSeleccionado(usuario);
  };

  const handleFilterChange = (e) => {
    setFiltro(e.target.value);
  };

  const handleCriterioChange = (e) => {
    setCriterio(e.target.value);
  };

  const aplicarFiltro = () => {
    const filtered = users.filter((usuario) =>
      usuario[criterio]?.toString().toLowerCase().includes(filtro.toLowerCase())
    );
    setFilteredUsers(filtered);
  };

  const deshacerFiltro = () => {
    setFilteredUsers(users);
    setFiltro("");
  };

  const handleShowModal = () => {
    setShowModal(true);
    setUsuarioSeleccionado(null);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleCloseDashboard = () => {
    setUsuarioSeleccionado(null);
  };

  const handleEliminarUsuario = async (id) => {
    const confirmacion = window.confirm("¿Estás seguro de que deseas eliminar este usuario?");
    if (confirmacion) {
      try {
        const respuesta = await eliminarUsuario(id);
        if (respuesta) {
          await fetchData();
          deshacerFiltro();
          setUsuarioSeleccionado(null);
        } else {
          console.error("Error al eliminar el usuario");
        }
      } catch (error) {
        console.error("Error en la solicitud de eliminación:", error);
      }
    }
  };

  return (
    <div className="principal-container">
      <div className="secondary-container">
        <div className="filter-container">
          <div className="filter-container-header">
            <p>Filtrar</p>
          </div>
          <div className="filter-container-group">
            <select value={criterio} onChange={handleCriterioChange}>
              <option value="id_usuario">ID</option>
              <option value="nombre_usuario">Nombre</option>
              <option value="numrun_cliente">RUT</option>
              <option value="id_empleado">ID Empleado</option>
              <option value="id_tipo_usuario">Tipo de Usuario</option>
              <option value="fec_creacion_usuario">Fecha de Creación</option>
            </select>
            <input
              type="text"
              value={filtro}
              onChange={handleFilterChange}
              placeholder="Ingrese filtro..."
            />
          </div>
          <div className="filter-container-button">
            <button onClick={aplicarFiltro}>Filtrar</button>
            <button onClick={deshacerFiltro}>Deshacer</button>
          </div>
        </div>
        <div className="control-buttons-container">
          <div className="control-buttons-container-group">
            <button onClick={handleShowModal}>Agregar Usuario</button>
            <button>PDF</button>
            <button>Excel</button>
          </div>
        </div>
        <div className="table-container">
          <table className="table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Nombre</th>
                <th>RUT</th>
                <th>ID Empleado</th>
                <th>Tipo de Usuario</th>
                <th>Fecha de Creación</th>
              </tr>
            </thead>
            <tbody>
              {filteredUsers.map((usuario) => (
                <tr
                  key={usuario.id_usuario}
                  onClick={() => seleccionarUsuario(usuario)}
                  className="table-row"
                >
                  <td>{usuario.id_usuario}</td>
                  <td>{usuario.nombre_usuario}</td>
                  <td>{usuario.numrun_cliente}</td>
                  <td>{usuario.id_empleado}</td>
                  <td>{roles[usuario.id_tipo_usuario]?.desc_tipo_usuario || 'Cargando...'}</td>
                  <td>{usuario.fec_creacion_usuario}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      {showModal && <FormAgregarUsuario onClose={handleCloseModal} />}
      {usuarioSeleccionado && (
        <DashboardDetailsUsuario
          usuarioSeleccionado={usuarioSeleccionado}
          onClose={handleCloseDashboard}
          onEliminar={handleEliminarUsuario}
          onUpdateUsuario={fetchData}
        />
      )}
    </div>
  );
};

export default GestionUsuarios;
