import React, { useState } from 'react';
import axios from 'axios';

const UserComponent = ({ user }) => {
  const [image, setImage] = useState(null);
  const [showConfirmDialog, setShowConfirmDialog] = useState(false);

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleSave = () => {
    setShowConfirmDialog(true);
  };

  const handleConfirmSave = () => {
    const formData = new FormData();
    formData.append('image', image);
    formData.append('status', 'new status'); // Cambia 'new status' por el nuevo estado deseado

    axios.post('/api/user/update', formData)
      .then(response => {
        console.log('User updated successfully:', response.data);
        // Aquí puedes añadir lógica adicional si la solicitud fue exitosa
      })
      .catch(error => {
        console.error('There was an error updating the user!', error);
      });

    setShowConfirmDialog(false);
  };

  const handleCancel = () => {
    setShowConfirmDialog(false);
  };

  return (
    <div>
      <input type="file" onChange={handleImageChange} />
      <button onClick={handleSave}>Guardar</button>
      <button onClick={handleCancel}>Cancelar</button>

      {showConfirmDialog && (
        <div className="confirm-dialog">
          <p>¿Estás seguro de que deseas guardar los cambios?</p>
          <button onClick={handleConfirmSave}>Sí</button>
          <button onClick={handleCancel}>No</button>
        </div>
      )}
    </div>
  );
};

export default UserComponent;
