import React, { useState } from 'react';
import './CrearVisitaTecnica.css'

export const CrearVisitaTecnica = () => {
  const [formData, setFormData] = useState({
    id_vt: '',
    desc_vt: '',
    desc_problema_vt: '',
    analisis_vt: '',
    recomendacion_vt: '',
    beneficio_vt: '',
    proximospasos_vt: '',
    fec_creacion_vt: '',
    numrun_cliente: '',
    id_empleado: '',
    id_ot: '',
    id_estado_vt: '',
  });

  const empleados = [
    { id: 1, nombre: 'Empleado 1' },
    { id: 2, nombre: 'Empleado 2' },
    // Añadir más empleados según sea necesario
  ];

  const estados = [
    { id: 1, nombre: 'Estado 1' },
    { id: 2, nombre: 'Estado 2' },
    // Añadir más estados según sea necesario
  ];

  const clientes = [
    { id: 1, nombre: 'Cliente 1' },
    { id: 2, nombre: 'Cliente 2' },
    // Añadir más clientes según sea necesario
  ];

  const ots = [
    { id: 1, nombre: 'OT 1' },
    { id: 2, nombre: 'OT 2' },
    // Añadir más órdenes de trabajo según sea necesario
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Lógica para manejar el envío del formulario
    console.log(formData);
  };

  return (
    <div className='crear-visita-tecnica-container'>
      <div className='crear-visita-tecnica-principal'>
        <div className='crear-visita-tecnica-header'>
          <h1>hola</h1>
        </div>
        <div className='crear-visita-tecnica-content'>
          <div className='crear-visita-tecnica-form-container'>
            <form onSubmit={handleSubmit}>
              <label>
                ID Visita Técnica:
                <input type="text" name="id_vt" value={formData.id_vt} onChange={handleChange} />
              </label>
              <label>
                Descripción:
                <textarea name="desc_vt" value={formData.desc_vt} onChange={handleChange}></textarea>
              </label>
              <label>
                Descripción del Problema:
                <textarea name="desc_problema_vt" value={formData.desc_problema_vt} onChange={handleChange}></textarea>
              </label>
              <label>
                Análisis:
                <textarea name="analisis_vt" value={formData.analisis_vt} onChange={handleChange}></textarea>
              </label>
              <label>
                Recomendación:
                <textarea name="recomendacion_vt" value={formData.recomendacion_vt} onChange={handleChange}></textarea>
              </label>
              <label>
                Beneficio:
                <textarea name="beneficio_vt" value={formData.beneficio_vt} onChange={handleChange}></textarea>
              </label>
              <label>
                Próximos Pasos:
                <textarea name="proximospasos_vt" value={formData.proximospasos_vt} onChange={handleChange}></textarea>
              </label>
              <label>
                Fecha de Creación:
                <input type="date" name="fec_creacion_vt" value={formData.fec_creacion_vt} onChange={handleChange} />
              </label>
              <label>
                Cliente:
                <select name="numrun_cliente" value={formData.numrun_cliente} onChange={handleChange}>
                  {clientes.map((cliente) => (
                    <option key={cliente.id} value={cliente.id}>
                      {cliente.nombre}
                    </option>
                  ))}
                </select>
              </label>
              <label>
                Empleado:
                <select name="id_empleado" value={formData.id_empleado} onChange={handleChange}>
                  {empleados.map((empleado) => (
                    <option key={empleado.id} value={empleado.id}>
                      {empleado.nombre}
                    </option>
                  ))}
                </select>
              </label>
              <label>
                Orden de Trabajo:
                <select name="id_ot" value={formData.id_ot} onChange={handleChange}>
                  {ots.map((ot) => (
                    <option key={ot.id} value={ot.id}>
                      {ot.nombre}
                    </option>
                  ))}
                </select>
              </label>
              <label>
                Estado de Visita Técnica:
                <select name="id_estado_vt" value={formData.id_estado_vt} onChange={handleChange}>
                  {estados.map((estado) => (
                    <option key={estado.id} value={estado.id}>
                      {estado.nombre}
                    </option>
                  ))}
                </select>
              </label>
            </form>

          </div>

        </div>
        <div className='crear-visita-tecnica-footer'>
          <button type="submit">Enviar</button>
        </div>
      </div>

      <div className='crear-visita-tecnica-data-empleado'>

      </div>
    </div>


  );
};
