import React, { useEffect, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import "./UserInfo.css";

export const UserInfo = () => {
    const [username, setUsername] = useState('');
    const [nombreEmpleado, setNombreEmpleado] = useState('');
    const [tipoDeUsuario, setTipoDeUsuario] = useState('');
    const [rol, setRol] = useState('');
    const [profileImage, setProfileImage] = useState('');

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem('usuario'));
        const storedEmpleado = JSON.parse(localStorage.getItem('empleado'));
        const storedRol = JSON.parse(localStorage.getItem('rol'));
        const storedTipoUsuario = JSON.parse(localStorage.getItem('tipoUsuario'));

        if (storedUser) {
            setUsername(storedUser.nombre_usuario);
            setNombreEmpleado(storedEmpleado.pnombre + " " + storedEmpleado.apaterno + " " + storedEmpleado.amaterno);
            setTipoDeUsuario(storedTipoUsuario.desc_tipo_usuario);
            setRol(storedRol.desc_rol);
            setProfileImage(storedUser.profile_image || '../img/isotipo-arsheep.png');
        }
    }, []);

    const onDrop = async (acceptedFiles) => {
        const file = acceptedFiles[0];
        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await axios.post('http://localhost:3001/api/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });

            const newImageUrl = response.data.url;
            setProfileImage(newImageUrl);
            const storedUser = JSON.parse(localStorage.getItem('usuario'));
            storedUser.profile_image = newImageUrl;
            localStorage.setItem('usuario', JSON.stringify(storedUser));

            // Actualizar imagen en la base de datos
            await axios.put(`http://localhost:3001/api/users/${storedUser.id}`, {
                profile_image: newImageUrl
            });
        } catch (error) {
            console.error('Error al subir la imagen:', error);
        }
    };

    const { getRootProps, getInputProps } = useDropzone({ onDrop });

    return (
        <div className='user-info'>
            <p>{tipoDeUsuario}</p>
            <div className='user-info-img' {...getRootProps()}>
                <input {...getInputProps()} />
                <img src={profileImage} alt={`Imagen de ${username}`} />
            </div>
            <p>{nombreEmpleado}</p>
            <p>{rol}</p>
        </div>
    );
};
