import { getTiposClientes, createCliente, subirImgenCliente } from '../../../services/clienteService';

export const ClienteCrear = () => {
    const location = useLocation();
    const [selectedImage, setSelectedImage] = useState('../../img/no_photo_arsheep.png');
    const [fileImage, setFileImage] = useState();
    const [rut, setRut] = useState('');
    const [dvrun, setDvrun] = useState('');
    const [nombre, setNombre] = useState('');
    const [appaterno, setAppaterno] = useState('');
    const [apmaterno, setApmaterno] = useState('');
    const [email, setEmail] = useState('');
    const [numTelefono, setNumTelefono] = useState('');
    const [comuna, setComuna] = useState('');
    const [descComuna, setDescComuna] = useState('');
    const [fechaNacimiento, setFechaNacimiento] = useState('');
    const [tipoCliente, setTipoCliente] = useState('');
    const [imagen, setImagen] = useState('');
    const [fechaInicioContrato, setFechaInicioContrato] = useState('');
    const [fechaTerminoContrato, setFechaTerminoContrato] = useState('');
    const [fechaCreacion, setFechaCreacion] = useState('1999-01-01');
    const [region, setRegion] = useState('');
    const [direccion, setDireccion] = useState('');
    const [regionesList, setRegionesList] = useState([]);
    const [comunasList, setComunasList] = useState([]);
    const [tiposClienteList, setTiposClienteList] = useState([]);

    const formatRUT = (input) => {
        let value = input.replace(/[^0-9kK]/g, '').toUpperCase();
        if (value.includes('K') && value.indexOf('K') !== value.length - 1) {
            value = value.replace(/K/g, '');
        }
        if (value.length > 9) {
            value = value.slice(0, 8) + value.slice(8, 9);
        }
        if (value.length > 1) {
            value = value.slice(0, -1) + '-' + value.slice(-1);
        }
        if (value.length > 5) {
            value = value.slice(0, -5) + '.' + value.slice(-5);
        }
        if (value.length > 9) {
            value = value.slice(0, -9) + '.' + value.slice(-9);
        }
        if (value.length > 12) {
            value = value.slice(0, 12);
        }
        setRut(value);
    };

    useEffect(() => {
        const fetchRegiones = async () => {
            try {
                const regiones = await getRegiones();
                setRegionesList(regiones);
            } catch (error) {
                console.error('Error al obtener las regiones:', error);
            }
        };

        const fetchComunas = async () => {
            try {
                const comunas = await getComunasByRegion(region);
                setComunasList(comunas);
            } catch (error) {
                console.error('Error al obtener las comunas:', error);
            }
        };

        const fetchTiposCliente = async () => {
            try {
                const tiposCliente = await getTiposClientes();
                setTiposClienteList(tiposCliente);
            } catch (error) {
                console.error('Error al obtener los tipos de cliente:', error);
            }
        };

        fetchRegiones();
        fetchComunas();
        fetchTiposCliente();
    }, [region]);

    const formatInputText = (input, maxLength) => {
        let formattedName = input.toUpperCase().replace(/[a-z]/g, '').slice(0, maxLength);
        return formattedName;
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const [numrun_cliente, dvrun_cliente] = rut.split('-');
            const formattedNumrun = numrun_cliente.replace(/\./g, '');

            let clienteData = {
                numrun_cliente: formattedNumrun,
                dvrun_cliente: "k",
                nombre_cliente: "orlandoooooooo",
                appaterno_cliente: "ROMERO",
                apmaterno_cliente: "JARA",
                email_cliente: "javier@gmail.com",
                direccion_cliente: "Calle Nueva 123",
                id_comuna: 1,
                numtelefono_cliente: "985643242",
                fecnac_cliente: "1991-02-03",
                id_tipo_cliente: 2,
                fec_creacion_cliente: "1995-02-02",
                id_region: 1
            };

            const response = await createCliente(clienteData);
            console.log(clienteData);
            if (response) {
                alert("Datos del cliente creados con éxito");
                console.log(clienteData);
            } else {
                alert("No se pudieron crear los datos del cliente");
            }
        } catch (error) {
            alert("No se pudieron crear los datos del cliente");
            console.error(error);
        }
    };

    const handleImageChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const validImageTypes = ['image/jpeg', 'image/png', 'image/gif'];
            if (!validImageTypes.includes(file.type)) {
                alert("Solo se permiten archivos de imagen (jpeg, png, gif).");
                return;
            }

            const reader = new FileReader();
            reader.onloadend = () => {
                setSelectedImage(reader.result);
                setFileImage(file);
                handleImageUpload(file);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleImageUpload = async (file) => {
        if (!file) return;

        const formData = new FormData();
        formData.append('image', file);

        try {
            const response = await subirImgenCliente(formData);
            if (response && response.newImageUrl) {
                setImagen(response.newImageUrl);
                console.log('Imagen subida con éxito:', response.newImageUrl);
            } else {
                console.error('La respuesta del servidor no contiene la URL de la imagen');
            }
        } catch (error) {
            console.error('Error al subir la imagen:', error);
        }
    };

    return (
        <div className='principal-container'>
            <div className='secondary-container'>
                <div className='container-header'>
                    <h1>Crear Cliente</h1>
                </div>
                <div className='form-container'>
                    <form onSubmit={handleSubmit}>
                        <div className='card-image'>
                            <img
                                src={selectedImage}
                                alt={`Imagen de ${""}`}
                                onClick={() => document.getElementById('imageUpload').click()}
                            />
                            <input
                                type="file"
                                id="imageUpload"
                                style={{ display: 'none' }}
                                onChange={handleImageChange}
                            />
                        </div>
                        <div className='form-level'>
                            <div className='form-group'>
                                <label htmlFor="rut" className="label-rut">RUT</label>
                                <input
                                    id="rut"
                                    className="input-rut"
                                    type="text"
                                    value={rut}
                                    onChange={(e) => formatRUT(e.target.value)}
                                    required
                                />
                            </div>
                        </div>
                        <div className='form-level'>
                            <div className='form-group'>
                                <label htmlFor="firstName" className="label-name">NOMBRE</label>
                                <input
                                    id="firstName"
                                    className="input-name"
                                    type="text"
                                    value={nombre}
                                    onChange={(e) => setNombre(formatInputText(e.target.value, 30))}
                                    required
                                />
                            </div>
                        </div>
                        <div className='form-level'>
                            <div className='form-group'>
                                <label htmlFor="lastName" className="label-name">APELLIDO</label>
                                <input
                                    id="lastName"
                                    className="input-name"
                                    type="text"
                                    value={appaterno}
                                    onChange={(e) => setAppaterno(formatInputText(e.target.value, 30))}
                                    required
                                />
                            </div>
                        </div>
                        <div className='form-level'>
                            <div className='form-group'>
                                <label htmlFor="secondLastName" className="label-name">SEGUNDO APELLIDO</label>
                                <input
                                    id="secondLastName"
                                    className="input-name"
                                    type="text"
                                    value={apmaterno}
                                    onChange={(e) => setApmaterno(formatInputText(e.target.value, 30))}
                                />
                            </div>
                        </div>
                        <div className='form-level'>
                            <div className='form-group'>
                                <label htmlFor="birthDate" className="label-birthDate">FECHA DE NACIMIENTO</label>
                                <input
                                    id="birthDate"
                                    className="input-birthDate"
                                    type="date"
                                    value={fechaNacimiento}
                                    onChange={(e) => setFechaNacimiento(e.target.value)}
                                    required
                                />
                            </div>
                        </div>
                        <div className='form-level'>
                            <div className='form-group'>
                                <label htmlFor="address" className="label-address">DIRECCIÓN</label>
                                <input
                                    id="address"
                                    className="input-address"
                                    type="text"
                                    value={direccion}
                                    onChange={(e) => setDireccion(formatInputText(e.target.value, 60))}
                                    required
                                />
                            </div>
                        </div>
                        <div className='form-level'>
                            <div className='form-group'>
                                <label htmlFor="region" className="label-region">REGIÓN</label>
                                <select
                                    id="region"
                                    className="input-region"
                                    value={region}
                                    onChange={(e) => setRegion(e.target.value)}
                                    required
                                >
                                    <option value="">Seleccione una región</option>
                                    {regionesList.map((region) => (
                                        <option key={region.id_region} value={region.id_region}>
                                            {region.desc_region}
                                        </option>
                                    ))}
                                </select>
                            </div>
                        </div>
                        <div className='form-level'>
                            <div className='form-group'>
                                <label htmlFor="comuna" className="label-comuna">COMUNA</label>
                                <select
                                    id="comuna"
                                    className="input-comuna"
                                    value={comuna}
                                    onChange={(e) => setComuna(e.target.value)}
                                    required
                                >
                                    <option value="">Seleccione una comuna</option>
                                    {comunasList.map((comuna) => (
                                        <option key={comuna.id_comuna} value={comuna.id_comuna}>
                                            {comuna.desc_comuna}
                                        </option>
                                    ))}
                                </select>
                            </div>
                        </div>
                        <div className='form-level'>
                            <div className='form-group'>
                                <label htmlFor="email" className="label-email">EMAIL</label>
                                <input
                                    id="email"
                                    className="input-email"
                                    type="email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                />
                            </div>
                        </div>
                        <div className='form-level'>
                            <div className='form-group'>
                                <label htmlFor="phone" className="label-phone">TELÉFONO</label>
                                <input
                                    id="phone"
                                    className="input-phone"
                                    type="text"
                                    value={numTelefono}
                                    onChange={(e) => setNumTelefono(e.target.value)}
                                    required
                                />
                            </div>
                        </div>
                        <div className='form-level'>
                            <div className='form-group'>
                                <label htmlFor="tipocliente" className="label-tipocliente">TIPO DE CLIENTE</label>
                                <select
                                    id="tipocliente"
                                    className="input-tipocliente"
                                    value={tipoCliente}
                                    onChange={(e) => setTipoCliente(e.target.value)}
                                    required
                                >
                                    <option value="">Seleccione un tipo de cliente</option>
                                    {tiposClienteList.map((tipocliente) => (
                                        <option key={tipocliente.id_tipo_cliente} value={tipocliente.id_tipo_cliente}>
                                            {tipocliente.desc_tipo_cliente}
                                        </option>
                                    ))}
                                </select>
                            </div>
                        </div>
                        <div className='form-level'>
                            <div className='form-group'>
                                <label htmlFor="startDate" className="label-startDate">INICIO DE CONTRATO</label>
                                <input
                                    id="startDate"
                                    className="input-startDate"
                                    type="date"
                                    value={fechaInicioContrato}
                                    onChange={(e) => setFechaInicioContrato(e.target.value)}
                                />
                            </div>
                        </div>
                        <div className='form-level'>
                            <div className='form-group'>
                                <label htmlFor="endDate" className="label-endDate">TÉRMINO DE CONTRATO</label>
                                <input
                                    id="endDate"
                                    className="input-endDate"
                                    type="date"
                                    value={fechaTerminoContrato}
                                    onChange={(e) => setFechaTerminoContrato(e.target.value)}
                                />
                            </div>
                        </div>
                        <button type="submit" className="submit">Guardar Datos</button>
                    </form>
                </div>
            </div>
        </div>
    );
};
