// components/LocationSelector.js
import React, { useEffect, useState } from 'react';
import { obtenerRegiones, obtenerComunasPorRegion } from '../../services/LocationService';

export const LocationSelector = ({ onChange }) => {
    const [regiones, setRegiones] = useState([]);
    const [comunas, setComunas] = useState([]);
    const [selectedRegion, setSelectedRegion] = useState('');

    useEffect(() => {
        const fetchRegiones = async () => {
            const regionesData = await obtenerRegiones();
            setRegiones(regionesData);
        };

        fetchRegiones();
    }, []);

    const handleRegionChange = async (event) => {
        const id_region = event.target.value;
        setSelectedRegion(id_region);
        const comunasData = await obtenerComunasPorRegion(id_region);
        setComunas(comunasData);
        onChange({ region: id_region, comuna: '' }); // Reset comuna when region changes
    };

    const handleComunaChange = (event) => {
        onChange({ region: selectedRegion, comuna: event.target.value });
    };

    return (
        <div>
            <label>Región:</label>
            <select value={selectedRegion} onChange={handleRegionChange}>
                <option value="">Seleccione una región</option>
                {regiones.map((region) => (
                    <option key={region.id_region} value={region.id_region}>
                        {region.desc_region}
                    </option>
                ))}
            </select>

            {selectedRegion && (
                <>
                    <label>Comuna:</label>
                    <select onChange={handleComunaChange}>
                        <option value="">Seleccione una comuna</option>
                        {comunas.map((comuna) => (
                            <option key={comuna.id_comuna} value={comuna.id_comuna}>
                                {comuna.desc_comuna}
                            </option>
                        ))}
                    </select>
                </>
            )}
        </div>
    );
};
