import React, { useState, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import './ClienteFicha.css';

export const ClienteFicha = () => {
    const location = useLocation();
    const { cliente } = location.state || {};

    const [rut, setRut] = useState('');
    const nameRef = useRef(null);
    const addressRef = useRef(null);
    const emailRef = useRef(null);
    const phoneRef = useRef(null);
    const birthDateRef = useRef(null);
    const comunaRef = useRef(null);
    const regionRef = useRef(null);
    const clientTypeRef = useRef(null);
    const startDateRef = useRef(null);
    const endDateRef = useRef(null);
    const creationDateRef = useRef(null);

    const formatRUT = (input) => {
        let value = input.replace(/\D/g, ''); // Eliminar caracteres no numéricos
        if (value.length > 1) {
            value = value.slice(0, -1) + '-' + value.slice(-1); // Agregar guion
        }
        if (value.length > 5) {
            value = value.slice(0, -5) + '.' + value.slice(-5); // Agregar primer punto
        }
        if (value.length > 9) {
            value = value.slice(0, -9) + '.' + value.slice(-9); // Agregar segundo punto
        }
        setRut(value);
    };

    const handleKeyPress = (event, ref) => {
        if (event.key === 'Enter') {
            event.preventDefault();
            ref.current.focus();
        }
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const [numrun_cliente, dvrun_cliente] = rut.split('-');
        const formattedNumrun = numrun_cliente.replace(/\./g, '');
        // Aquí puedes enviar `formattedNumrun` y `dvrun_cliente` a tu backend o guardarlos en tu estado
        console.log(`numrun_cliente: ${formattedNumrun}, dvrun_cliente: ${dvrun_cliente}`);
    };

    return (
        <div className='principal-container'>
            <div className='secondary-container'>
                <div className='container-header'>
                    <h1>Ficha del Cliente</h1>
                </div>
                <div className='form-container'>
                    {cliente ? (
                        <form onSubmit={handleSubmit}>
                            <div className="form-group">
                                <label>RUT</label>
                                <input 
                                    type="text" 
                                    value={rut} 
                                    onChange={(e) => formatRUT(e.target.value)} 
                                    onKeyPress={(e) => handleKeyPress(e, nameRef)} 
                                    required 
                                />
                            </div>
                            <div className="form-group">
                                <label>Nombre</label>
                                <input 
                                    type="text" 
                                    value={`${cliente.pnombre_cliente} ${cliente.snombre_cliente} ${cliente.appaterno_cliente} ${cliente.apmaterno_cliente}`} 
                                    readOnly 
                                    ref={nameRef} 
                                    onKeyPress={(e) => handleKeyPress(e, addressRef)}
                                />
                            </div>
                            <div className="form-group">
                                <label>Dirección</label>
                                <input 
                                    type="text" 
                                    value={cliente.dirección_cliente} 
                                    readOnly 
                                    ref={addressRef} 
                                    onKeyPress={(e) => handleKeyPress(e, emailRef)}
                                />
                                                                <label>Comuna</label>
                                <input 
                                    type="text" 
                                    value={cliente.COMUNA.desc_comuna} 
                                    readOnly 
                                    ref={comunaRef} 
                                    onKeyPress={(e) => handleKeyPress(e, regionRef)}
                                />
                                                                <label>Región</label>
                                <input 
                                    type="text" 
                                    value={cliente.COMUNA.id_region} 
                                    readOnly 
                                    ref={regionRef} 
                                    onKeyPress={(e) => handleKeyPress(e, clientTypeRef)}
                                />
                            </div>
                            <div className="form-group">
                                <label>Email</label>
                                <input 
                                    type="text" 
                                    value={cliente.email_cliente} 
                                    readOnly 
                                    ref={emailRef} 
                                    onKeyPress={(e) => handleKeyPress(e, phoneRef)}
                                />
                            </div>
                            <div className="form-group">
                                <label>Teléfono</label>
                                <input 
                                    type="text" 
                                    value={cliente.numtelefono_cliente} 
                                    readOnly 
                                    ref={phoneRef} 
                                    onKeyPress={(e) => handleKeyPress(e, birthDateRef)}
                                />
                            </div>
                            <div className="form-group">
                                <label>Fecha de Nacimiento</label>
                                <input 
                                    type="text" 
                                    value={new Date(cliente.fecnac_cliente).toLocaleDateString()} 
                                    readOnly 
                                    ref={birthDateRef} 
                                    onKeyPress={(e) => handleKeyPress(e, comunaRef)}
                                />
                            </div>
                            <div className="form-group">

                            </div>
                            <div className="form-group">

                            </div>
                            <div className="form-group">
                                <label>Tipo de Cliente</label>
                                <input 
                                    type="text" 
                                    value={cliente.TIPO_CLIENTE.desc_tipo_cliente} 
                                    readOnly 
                                    ref={clientTypeRef} 
                                    onKeyPress={(e) => handleKeyPress(e, startDateRef)}
                                />
                            </div>
                            <div className="form-group">
                                <label>Fecha de Inicio de Contrato</label>
                                <input 
                                    type="text" 
                                    value={new Date(cliente.fec_inicio_contrato_cliente).toLocaleDateString()} 
                                    readOnly 
                                    ref={startDateRef} 
                                    onKeyPress={(e) => handleKeyPress(e, endDateRef)}
                                />
                            </div>
                            <div className="form-group">
                                <label>Fecha de Término de Contrato</label>
                                <input 
                                    type="text" 
                                    value={new Date(cliente.fec_termino_contrato_cliente).toLocaleDateString()} 
                                    readOnly 
                                    ref={endDateRef} 
                                    onKeyPress={(e) => handleKeyPress(e, creationDateRef)}
                                />
                            </div>
                            <div className="form-group">
                                <label>Fecha de Creación del Cliente</label>
                                <input 
                                    type="text" 
                                    value={new Date(cliente.fec_creacion_cliente).toLocaleDateString()} 
                                    readOnly 
                                    ref={creationDateRef}
                                />
                            </div>
                            <button type="submit">Guardar RUT</button>
                        </form>
                    ) : (
                        <p>No se ha seleccionado ningún cliente.</p>
                    )}
                </div>
            </div>
        </div>
    );
};
