// CalendarComponent.js
import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import supabase from './supabaseClient';

const PruebaCalendario = () => {
  const [date, setDate] = useState(new Date());
  const [availableTimes, setAvailableTimes] = useState([]);
  const [selectedTime, setSelectedTime] = useState('');

  useEffect(() => {
    fetchAvailableTimes(date);
  }, [date]);

  const fetchAvailableTimes = async (selectedDate) => {
    const { data, error } = await supabase
      .from('availability')
      .select('*')
      .eq('date', selectedDate.toISOString().split('T')[0]);
    
    if (error) {
      console.error('Error fetching available times:', error);
    } else {
      setAvailableTimes(data);
    }
  };

  const handleDateChange = (newDate) => {
    setDate(newDate);
    setSelectedTime('');
  };

  const handleTimeSelect = (time) => {
    setSelectedTime(time);
  };

  const handleBooking = async () => {
    if (!selectedTime) {
      alert('Please select a time');
      return;
    }

    const { data, error } = await supabase
      .from('bookings')
      .insert([{ date: date.toISOString().split('T')[0], time: selectedTime, user_id: 'your-user-id' }]);

    if (error) {
      console.error('Error creating booking:', error);
    } else {
      alert('Booking successful');
      fetchAvailableTimes(date); // Refresh available times
    }
  };

  return (
    <div>
      <h1>Select a date and time</h1>
      <Calendar onChange={handleDateChange} value={date} />
      <div>
        <h2>Available Times</h2>
        <ul>
          {availableTimes.map((time) => (
            <li key={time.id} onClick={() => handleTimeSelect(time.time)}>
              {time.time}
            </li>
          ))}
        </ul>
      </div>
      {selectedTime && (
        <div>
          <h3>Selected Time: {selectedTime}</h3>
          <button onClick={handleBooking}>Book</button>
        </div>
      )}
    </div>
  );
};

export default PruebaCalendario;
